/*
 * remove default editor from pages
*/
add_action('admin_init', 'hide_editor');
function hide_editor(){
	if( isset( $_POST['post_ID'] ) ){ $post_id = (int)$_POST['post_ID']; }
	if( isset( $_GET['post'] )     ){ $post_id = (int)$_GET['post'];     }
	if( !isset( $post_id ) ) return;
	$post_type = get_post_type( $post_id );
	if( in_array( $post_type, array( 'location', 'service', ) ) ){
		//remove_post_type_support( 'page', 'editor' );
		add_filter( 'use_block_editor_for_post', '__return_false', 10 );
	}
}

/*
 * check heve state/city location
 */
function is_state_city_have_locations( &$arr, $state, $city ){
	foreach( $arr['locations'] as $locations_item ) {
		if ( $locations_item['state_name'] == $state && $locations_item['city_name'] == $city ) {
			return true;
		}
	}
	return false;
}


